int BinarySearch(SeqList L, ElemType key){
    int low=0, high=L.length-1, mid;
    while(low <= high){
        mid = (low+high)/2;
        if(L.Elems[mid]==key) return mid;
        else if(L.Elems[mid] > key) high = mid - 1;
        else low = mid + 1;
    }
    return -1;
}

